class AddExperienceBtn extends Component {
  changeWorkExperienceMode = () => this.props.changeWorkExperienceMode();
  render() {
    return (
      <button onClick={this.changeWorkExperienceMode}>+ Experience</button>
    );
  }
}

class WorkExperience extends Component {
  changeWorkExperienceMode = () => {
    this.props.changeWorkExperienceMode();
  };
  // handleInputChange = (field, element) =>
  //   this.props.handleInputChange(field, element);
  // addNewExperience = () => this.props.addNewExperience();
  removeWorkExperience = (element) => this.props.removeWorkExperience(element);
  render() {
    const {
      modes,
      componentsArray,
      inputs,
      textDisplay
    } = this.props;
    const workExperienceArray =componentsArray.workExperienceArray;
    const rows = workExperienceArray.map((work) => (
      <ExperienceHistoryRow
        workExperienceArray={workExperienceArray}
        work={work}
        key={work.id}
        removeWorkExperience={this.removeWorkExperience}
      />
    ));
    let element;
    if (!modes.appMode.isEdit) { element = null};
    if (modes.appMode.isEdit && !modes.workExperienceMode.isEdit) {
      element = (
        <AddExperienceBtn
          changeWorkExperienceMode={this.changeWorkExperienceMode}
        />
      );
    }
    if (modes.appMode.isEdit && modes.workExperienceMode.isEdit) {
      element = (
        <AddExperienceForm
        inputCompanyName={inputs.inputCompanyName}
        inputCompanyCity={inputs.inputCompanyCity}
        inputCompanyFrom={inputFromText}
          inputToText={inputToText}
          inputJobDescriptionText={inputJobDescriptionText}
          changeWorkExperienceMode={this.changeWorkExperienceMode}
          handleInputChange={this.handleInputChange}
          addNewExperience={this.addNewExperience}
        />
      );
    }
    return (
      <div>
        <h2>Experiences</h2>
        {rows}
        {element}
      </div>
    );
  }
}

export default WorkExperience;
