import React, { Component } from 'react';

class ExperienceHistoryRow extends Component {
  removeWorkExperience 
  render() {
    const {work} = this.props;
    return (
    <div>
      <div>
        <p>{work.companyNameText}, {work.cityText}</p>
      </div>
      <div>
        <p>{work.fromText} - {work.toText}</p>
        <p>{work.jobDescriptionText}</p>
      </div>
      <button type='submit' onClick={this.removeWorkExperience}>X</button>
    </div>
    );
  }
}

class AddExperienceForm extends Component {
  changeWorkExperienceMode = (e) => {
    e.preventDefault();
    this.props.changeWorkExperienceMode();
  };
  handleInputChange = (e) => {
    let target = e.target.name;
    let field;
    if (target === 'companyName') {
      field = 'inputCompanyNameText';
    } else if (target === 'city') {
      field = 'inputCityText';
    } else if (target === 'from') {
      field = 'inputFromText';
    } else if (target === 'to') {
      field = 'inputToText';
    } else if (target === 'jobDescription') {
      field = 'inputJobDescriptionText';
    }
    this.props.handleInputChange(field, e.target.value);
  };
  addNewExperience = (e) => {
    e.preventDefault();
    this.props.addNewExperience();
  };
  render() {
    return (
      <form>
        <input
          type="text"
          placeholder="Company Name"
          autoComplete="off"
          name="companyName"
          value={this.props.inputCompanyNameText}
          onChange={this.handleInputChange}
        ></input>
        <input
          type="text"
          placeholder="City"
          autoComplete="off"
          name="city"
          value={this.props.inputCityText}
          onChange={this.handleInputChange}
        ></input>
        <input
          type="text"
          placeholder="From"
          autoComplete="off"
          name="from"
          value={this.props.inputFromText}
          onChange={this.handleInputChange}
        ></input>
        <input
          type="text"
          placeholder="To"
          autoComplete="off"
          name="to"
          value={this.props.inputToText}
          onChange={this.handleInputChange}
        ></input>
        <textarea
          rows="5"
          cols="30"
          name="jobDescription"
          placeholder="Job Description & Achievements"
          value={this.props.inputJobDescriptionText}
          onChange={this.handleInputChange}
        ></textarea>
        <button type="submit" onClick={this.changeWorkExperienceMode}>
          Cancel
        </button>
        <button type="submit" onClick={this.addNewExperience}>
          + Add
        </button>
      </form>
    );
  }
}
class AddExperienceBtn extends Component {
  changeWorkExperienceMode = () => this.props.changeWorkExperienceMode();
  render() {
    return (
      <button onClick={this.changeWorkExperienceMode}>+ Experience</button>
    );
  }
}

class WorkExperience extends Component {
  changeWorkExperienceMode = () => {
    this.props.changeWorkExperienceMode();
    this.props.turnOffUserInfoEditMode(this.props.userInfoArray);
  };
  handleInputChange = (field, element) =>
    this.props.handleInputChange(field, element);
  addNewExperience = () => this.props.addNewExperience();
  render() {
    const {
      appMode,
      workExperienceArray,
      workExperienceMode,
      inputCompanyNameText,
      inputCityText,
      inputFromText,
      inputToText,
      inputJobDescriptionText
    } = this.props;
    const row = this.props.workExperienceArray.map(work => <ExperienceHistoryRow workExperienceArray={workExperienceArray} work={work} key={work.id}/>)
    let element;
    if (appMode.isEdit && !workExperienceMode.isEdit) {
      element = (
        <AddExperienceBtn
          changeWorkExperienceMode={this.changeWorkExperienceMode}
        />
      );
    }
    if (appMode.isEdit && workExperienceMode.isEdit) {
      element = (
        <AddExperienceForm
          inputCompanyNameText={inputCompanyNameText}
          inputCityText={inputCityText}
          inputFromText={inputFromText}
          inputToText={inputToText}
          inputJobDescriptionText={inputJobDescriptionText}
          changeWorkExperienceMode={this.changeWorkExperienceMode}
          handleInputChange={this.handleInputChange}
          addNewExperience={this.addNewExperience}
        />
      );
    }
    return (
      <div>
        <h2>Experiences</h2>
        {row}
        {element}
      </div>
    );
  }
}

export default WorkExperience;
