import React from 'react';
import uniqid from 'uniqid';
import UserInfo from './UserInfo';
import WorkExperience from './WorkExperience';
// import Education from './Education';
// import Skills from './Skills';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      modes: {
        appMode: {
          isEdit: true
        },
        workExperienceMode: {
          isEdit: false
        },
        educationMode: {
          isEdit: false
        },
        skillsMode: {
          isEdit: false
        }
      },
      componentsArray: {
        workExperienceArray: [],
        educationArray: [],
        skillsArray: []
      },
      inputs: {
        userInfoComponentInput: {
          inputUserInfo: ''
        },
        workExperienceComponentInput: {
          inputCompanyName: '',
          inputCompanyCity: '',
          inputCompanyFrom: '',
          inputCompanyTo: '',
          inputCompanyRole: '',
          inputCompanyJobDescription: ''
        },
        educationComponentInput: {
          inputSchool: '',
          inputSchoolCity: '',
          inputSchoolFromYear: '',
          inputSchoolToYear: '',
          inputSchoolDegree: ''
        },
        skillsComponentInput: {
          inputSkillName: ''
        }
      },
      textDisplay: {
        textUserInfoComponent: {
          textFirstName: {
            text: 'First Name',
            id: uniqid(),
            isEdit: false
          },
          textLastName: {
            text: 'Last Name',
            id: uniqid(),
            isEdit: false
          },
          textAddress1: {
            text: 'Address 1',
            id: uniqid(),
            isEdit: false
          },
          textAddress2: {
            text: 'Address 2',
            id: uniqid(),
            isEdit: false
          },
          textPhone: {
            text: 'â˜Žï¸',
            id: uniqid(),
            isEdit: false
          },
          textEmail: {
            text: 'ðŸ“§',
            id: uniqid(),
            isEdit: false
          },
          textGithub: {
            text: 'â¤ï¸ (URL):',
            id: uniqid(),
            isEdit: false
          }
        },
        textWorkExperienceComponent: {
          textCompanyName: '',
          textCompanyCity: '',
          textCompanyFromYear: '',
          textCompanyToYear: '',
          textCompanyRole: '',
          textCompanyRoleDescription: ''
        },
        textEducationComponent: {
          textSchoolName: '',
          textSchoolCity: '',
          textSchoolFromYear: '',
          textSchoolToYear: '',
          textSchoolDegree: ''
        },
        textSkillsComponent: {
          textSkillName: ''
        }
      }
    };
  }

  changeInfoRowToEditMode = (element) => {
    const target = element.closest('li').getAttribute('datakey');
    const textUserInfoComponent = this.state.textDisplay.textUserInfoComponent;
    const tempObj = { ...textUserInfoComponent };
    for (const info in textUserInfoComponent) {
      target === info
        ? (tempObj[target].isEdit = true)
        : (tempObj[info].isEdit = false);
    }
    this.setState({
      textDisplay: {
        textUserInfoComponent: tempObj
      }
    });
  };

  changeModeToEdit = (componentMode) => {
    const tempModes = { ...this.state.modes };
    tempModes[componentMode].isEdit = true;
    this.setState({
      modes: tempModes
    });
  };

  changeModeToNonEdit = (componentMode) => {
    const tempModes = { ...this.state.modes };
    tempModes[componentMode].isEdit = false;
    this.setState({
      modes: tempModes
    });
  };

  changeUserInfoMode = () => {
    let modeState = this.state.userInfoMode.isEdit;
    modeState ? (modeState = false) : (modeState = true);
    this.setState({
      userInfoMode: {
        isEdit: modeState
      }
    });
  };

  handleInputChange = (component, componentProp, inputText) => {
    const tempObj = { ...this.state.inputs };
    tempObj[component][componentProp] = inputText;
    this.setState({
      inputs: tempObj
    });
  };

  findClickedTargetDatakey = (element) => {
    return element.closest('form').getAttribute('datakey');
  };

  handleUserInfoUpdateBtn = (element) => {
    const datakey = this.findClickedTargetDatakey(element);
    const textUserInfoComponent = this.state.textDisplay.textUserInfoComponent;
    const tempObj = { ...textUserInfoComponent };
    const inputUserInfo =
      this.state.inputs.userInfoComponentInput.inputUserInfo;
    tempObj[datakey].text = inputUserInfo;
    tempObj[datakey].isEdit = false;
    const tempInputs = { ...this.state.inputs };
    tempInputs.userInfoComponentInput.inputUserInfo = '';
    this.setState({
      [textUserInfoComponent]: tempObj,
      inputs: tempInputs
    });
  };

  addNewExperience = () => {
    const tempArr = { ...this.state.componentsArray };
    const tempModes = { ...this.state.modes };
    const tempInputs = {...this.state.inputs};
    const {
      inputCompanyName,
      inputCompanyCity,
      inputCompanyFrom,
      inputCompanyTo,
      inputCompanyRole,
      inputCompanyJobDescription
    } = this.state.inputs.workExperienceComponentInput;
    const work = {
      id: uniqid(),
      textCompanyName: inputCompanyName,
      textCompanyCity: inputCompanyCity,
      textCompanyFromYear: inputCompanyFrom,
      textCompanyToYear: inputCompanyTo,
      textCompanyRole: inputCompanyRole,
      textCompanyRoleDescription: inputCompanyJobDescription
    };
    tempArr.workExperienceArray.push(work);
    tempModes.workExperienceMode.isEdit = false;
    for (const property in tempInputs.workExperienceComponentInput) {
      tempInputs.workExperienceComponentInput[property] = '';
    }
    this.setState({
      componentsArray: tempArr,
      modes: tempModes,
      inputs: tempInputs
    });
  };

  removeWorkExperience = (element) => {
    const index = element.closest('.experienceContainer');
    const tempArr = {...this.state.componentsArray};
    tempArr.workExperienceArray.splice(index, 1)
    // this.setState({

    // });
  };

  changeWorkExperienceMode = () => {
    const tempModes = { ...this.state.modes };
    tempModes.workExperienceMode.isEdit
      ? (tempModes.workExperienceMode.isEdit = false)
      : (tempModes.workExperienceMode.isEdit = true);
    this.setState({
      modes: tempModes
    });
  };
  // changeEducationMode = () => {
  //   let modeState = this.state.educationMode.isEdit;
  //   modeState ? (modeState = false) : (modeState = true);
  //   this.setState({
  //     educationMode: {
  //       isEdit: modeState
  //     }
  //   });
  // };
  render() {
    const { modes, componentsArray, inputs, textDisplay } = this.state;
    return (
      <div className="appContainer">
        <nav>
          <button onClick={() => this.changeModeToEdit('appMode')}>
            EDIT MODE
          </button>
          <button onClick={() => this.changeModeToNonEdit('appMode')}>
            VIEW MODE
          </button>
        </nav>
        <UserInfo
          modes={modes}
          inputs={inputs.userInfoComponentInput.inputUserInfo}
          textDisplay={textDisplay}
          changeInfoRowToEditMode={this.changeInfoRowToEditMode}
          handleInputChange={this.handleInputChange}
          handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
        />
        <WorkExperience
          modes={modes}
          componentsArray={componentsArray}
          workExperienceComponentInput={inputs.workExperienceComponentInput}
          textWorkExperienceComponent={textDisplay.textWorkExperienceComponent}
          handleInputChange={this.handleInputChange}
          removeWorkExperience={this.removeWorkExperience}
          changeWorkExperienceMode={this.changeWorkExperienceMode}
          addNewExperience={this.addNewExperience}
        />
        {/* <Education />
        <Skills /> */}
      </div>
    );
  }
}

export default App;
