class BasicInfoPreviewMode extends Component {
  turnOnEditMode = (e) => {

  };
  render() {
    const { infoNameValueObj } = this.props;
    return (
      <p>
        {infoNameValueObj.text} <span onClick={this.turnOnEditMode}>âœŽ</span>
      </p>
    );
