}

class BasicInfo extends Component {
  turnOnEditMode = (e) => this.props.turnOnEditMode(e);
  render() {
    const userInfoArray = [];
    const { userInfo, mode } = this.props;
    for (const [infoName, infoNameValueObj] of Object.entries(userInfo)) {
      if (
        infoName === 'firstName' ||
        infoName === 'lastName' ||
        infoName === 'currentRole' ||
        infoName === 'addr' ||
        infoName === 'phone' ||
        infoName === 'email' ||
        infoName === 'github'
      ) {
        userInfoArray.push({ [infoName]: infoNameValueObj });
      } else return;
    }
    return (
      <div>
        {userInfoArray.map((userInfo) => {
          let display;
          const userBasicInfo = Object.entries(userInfo);
          if (mode.isPreview) {
            display = (
              <BasicInfoPreviewMode
                key={userBasicInfo[0][1].id}
                infoNameValueObj={userBasicInfo[0][1]}
                turnOnEditMode={this.turnOnEditMode}
              />
            );
          }
          return display;
        })}
      </div>
    );
  }
}

class ContactInfo extends Component {
  render() {
    return <div className="ContactInfo"></div>;
  }
}

class UserInfo extends Component {
  turnOnEditMode = () => this.props.turnOnEditMode();
  render() {
    const { mode, userInfo } = this.props;
    return (
      <div className="UserInfoContainer">
        <BasicInfo
          mode={mode}
          userInfo={userInfo}
          turnOnEditMode={this.turnOnEditMode}
        />
        <ContactInfo userInfo={this.props.userInfo} />
      </div>
    );
  }
}

export default UserInfo;
