import React, { Component } from 'react';
import UserInfo from './UserInfo';
import uniqid from 'uniqid';
import './Builder.css';

class Builder extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mode: {
        isEdit: false,
        isPreview: true,
        isView: false
      },
      userInfo: {
        firstName: {
          text: 'First Name',
          id: uniqid(),
          mode: {
            isEdit: false,
            isView: true
          }
        },
        lastName: {
          text: 'Last Name',
          id: uniqid(),
          mode: {
            isEdit: false,
            isView: true
          }
        },
        currentRole: {
          text: 'Full Stack Developer',
          id: uniqid()
        },
        addr: {
          text: 'Houston, TX',
          id: uniqid()
        },
        phone: {
          text: '(555) 555-5555',
          id: uniqid()
        },
        email: {
          text: 'yourname@email.com',
          id: uniqid()
        },
        github: {
          text: 'https://github.com',
          id: uniqid()
        }
      }
    };
  }
  turnOnEditMode = (e) => {
    this.setState({
      mode: {
        isEdit: true,
        isPreview: false,
        isView: false
      }
    });
  };
  handleInputText = (e) => {

  };
  handleUpdateBtn = (e) => {

  };
  render() {
    const {mode, userInfo} = this.state;
    return (
      <main>
        <UserInfo
          mode={mode}
          userInfo={userInfo}
          turnOnEditMode={this.turnOnEditMode}
          handleInputText={this.handleInputText}
          handleUpdateBtn={this.handleUpdateBtn}
        />
      </main>
    );
  }
}

export default Builder;
