import React, { Component } from 'react';

class AddExperienceForm extends Component {
  render() {
    return (
      <form>
        <input type='text' placeholder='Company Name'></input>
        <input type='text' placeholder='City'></input>
        <input type='text' placeholder='From'></input>
        <input type='text' placeholder='To'></input>
        <input type='textare' placeholder='Job Description and Achievement'></input>
        <input type='submit'>Cancel</input>
        <input type='submit'>+ Add</input>
      </form>
    )
  }
}
class AddExperienceBtn extends Component {
  changeWorkExperienceMode = () => this.props.changeWorkExperienceMode();
  render() {
    return <button onClick={this.changeWorkExperienceMode}>+ Experience</button>
  }
}

class WorkExperience extends Component {
  constructor(props) {
    super(props);
    this.state = {
      workExperienceMode: {
        isEdit: false
      }
    }
  }
  changeWorkExperienceMode = () => {
    this.setState({
      workExperienceMode: {
        isEdit
      }
    })
  }
  render() {
    const { appMode } = this.props; 
    const { workExperienceMode } = this.state;
    const rows = [];
    let element;
    if (appMode.isEdit && !workExperienceMode.isEdit) {
      element = <AddExperienceBtn changeWorkExperienceMode={this.changeWorkExperienceMode}/>
    }
    if (appMode.isEdit && workExperienceMode.isEdit) {
      element = <AddExperienceForm />
    }
    return (
      <div>
        <h2>Experiences</h2>
        {rows}
        {element}
      </div>
    )
  }
}

export default WorkExperience;
