import React, { Component } from 'react';
import UserInfo from './UserInfo';
import WorkExperience from './WorkExperience';
import './Builder.css';

class Builder extends Component {
  changeInfoRowToEditMode = (element, arr) => {
    this.props.changeInfoRowToEditMode(element, arr);
  };
  handleInputChange = (field, element) => {
    this.props.handleInputChange(field, element);
  };
  handleUserInfoUpdateBtn = (field, element) => {
    this.props.handleUserInfoUpdateBtn(field, element);
  };
  turnOffUserInfoEditMode = () => this.props.turnOffUserInfoEditMode
  addNewExperience = () => this.props.addNewExperience();
  render() {
    const {
      appMode,
      userInfoArray,
      inputText,
      workExperienceArray,
      inputCompanyNameText,
      inputCityText,
      inputFromText,
      inputToText,
      inputJobDescriptionText
    } = this.props;
    return (
      <main>
        <UserInfo
          appMode={appMode}
          inputText={inputText}
          userInfoArray={userInfoArray}
          changeInfoRowToEditMode={this.changeInfoRowToEditMode}
          handleInputChange={this.handleInputChange}
          handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
        />
        <WorkExperience
          appMode={appMode}
          inputCompanyNameText={inputCompanyNameText}
          inputCityText={inputCityText}
          inputFromText={inputFromText}
          inputToText={inputToText}
          inputJobDescriptionText={inputJobDescriptionText}
          workExperienceArray={workExperienceArray}
          handleInputChange={this.handleInputChange}
          turnOffUserInfoEditMode={this.turnOffUserInfoEditMode}
          addNewExperience={this.addNewExperience}
        />
      </main>
    );
  }
}

export default Builder;
