import React, { Component } from 'react';
import './UserInfo.css';

class InfoRowForm extends Component {
  handleInputChange = (e) => {
    this.props.handleInputChange('inputText', e.target.value);
  };
  handleUserInfoUpdateBtn = (e) => {
    e.preventDefault();
    this.props.handleUserInfoUpdateBtn('inputText', e.target);
  };
  render() {
    return (
      <form>
        <input
          type="text"
          autoComplete="off"
          onChange={this.handleInputChange}
          value={this.props.inputText}
        ></input>
        <button type="submit" onClick={this.handleUserInfoUpdateBtn}>
          Update
        </button>
      </form>
    );
  }
}

