import React, { Component } from 'react';

class AddExperienceForm extends Component {
  changeWorkExperienceMode = (e) => {
    e.preventDefault();
    this.props.changeWorkExperienceMode();
  };
  handleInputText = (e) => this.props.handleInputText(e.target.value);
  render() {
    return (
      <form>
        <input
          type="text"
          placeholder="Company Name"
          autoComplete="off"
          value={this.props.inputText}
          onChange={this.handleInputText}
        ></input>
        <input
          type="text"
          placeholder="City"
          autoComplete="off"
          value={this.props.inputText}
        ></input>
        <input
          type="text"
          placeholder="From"
          autoComplete="off"
          value={this.props.inputText}
        ></input>
        <input
          type="text"
          placeholder="To"
          autoComplete="off"
          value={this.props.inputText}
        ></input>
        <textarea
          rows="5"
          cols="30"
          placeholder="Job Description Achievements"
          value={this.props.inputText}
        ></textarea>
        <button type="submit" onClick={this.changeWorkExperienceMode}>
          Cancel
        </button>
        <button type="submit">+ Add</button>
      </form>
    );
  }
}
class AddExperienceBtn extends Component {
  changeWorkExperienceMode = () => this.props.changeWorkExperienceMode();
  render() {
    return (
      <button onClick={this.changeWorkExperienceMode}>+ Experience</button>
    );
  }
}

class WorkExperience extends Component {
  constructor(props) {
    super(props);
    this.state = {
      workExperienceMode: {
        isEdit: false
      }
    };
  }
  changeWorkExperienceMode = () => {
    let modeState = this.state.workExperienceMode.isEdit;
    modeState ? (modeState = false) : (modeState = true);
    this.setState({
      workExperienceMode: {
        isEdit: modeState
      }
    });
  };
  handleInputText 
  render() {
    const { appMode } = this.props;
    const { workExperienceMode } = this.state;
    const rows = [];
    let element;
    if (appMode.isEdit && !workExperienceMode.isEdit) {
      element = (
        <AddExperienceBtn
          changeWorkExperienceMode={this.changeWorkExperienceMode}
        />
      );
    }
    if (appMode.isEdit && workExperienceMode.isEdit) {
      element = (
        <AddExperienceForm
          inputText={this.props.inputText}
          changeWorkExperienceMode={this.changeWorkExperienceMode}
          handleInputText={this.handleInputText}
        />
      );
    }
    return (
      <div>
        <h2>Experiences</h2>
        {rows}
        {element}
      </div>
    );
  }
}

export default WorkExperience;
