import React, { Component } from 'react';

class BasicInfoEditMode extends Component {
  handleInputText = (e) => this.props.handleInputText(e);
  handleUpdateBtn = (e) => this.props.handleUpdateBtn(e);
  render() {
    const { infoNameValueObj } = this.props;
    // let form;
    return (
      <form onSubmit={this.handleUpdateBtn}>
        <input
          type="text"
          autoComplete="off"
          onChange={this.handleInputText}
        />
        <button type="submit">Update</button>
      </form>
    );
  }
}

class BasicInfoPreviewMode extends Component {
  turnOnEditMode = (e) => this.props.turnOnEditMode(e);
  render() {
    const { infoNameValueObj } = this.props;
    return (
      <p>
        {infoNameValueObj.text} <span onClick={this.turnOnEditMode}>âœŽ</span>
      </p>
    );
  }
}

class BasicInfo extends Component {
  turnOnEditMode = (e) => this.props.turnOnEditMode(e);
  handleInputText = (e) => this.props.handleInputText(e);
  handleUpdateBtn = (e) => this.props.handleUpdateBtn(e);
  render() {
    const userInfoArray = [];
    const { userInfo, mode } = this.props;
    for (const [infoName, infoNameValueObj] of Object.entries(userInfo)) {
      if (
        infoName === 'firstName' ||
        infoName === 'lastName' ||
        infoName === 'currentRole' ||
        infoName === 'addr' ||
        infoName === 'phone' ||
        infoName === 'email' ||
        infoName === 'github'
      ) {
        userInfoArray.push({ [infoName]: infoNameValueObj });
      } else return;
    }
    return (
      <div>
        {userInfoArray.map((userInfo) => {
          let display;
          const userBasicInfo = Object.entries(userInfo);
          if (mode.isPreview) {
            display = (
              <BasicInfoPreviewMode
                key={userBasicInfo[0][1].id}
                infoNameValueObj={userBasicInfo[0][1]}
                turnOnEditMode={this.turnOnEditMode}
              />
            );
          }
          if (mode.isEdit) {
            console.log(e)
            // display = (<BasicInfoEditMode 
            //   key={userBasicInfo[0][1].id}
            //   infoNameValueObj={userBasicInfo[0][1]}
            //   handleInputText={this.handleInputText}
            //   handleUpdateBtn={this.handleUpdateBtn}/>)
          }
          return display;
        })}
      </div>
    );
  }
}

class ContactInfo extends Component {
  render() {
    return <div className="ContactInfo"></div>;
  }
}

class UserInfo extends Component {
  turnOnEditMode = () => this.props.turnOnEditMode();
  handleInputText = (e) => this.props.handleInputText(e);
  handleUpdateBtn = (e) => this.props.handleUpdateBtn(e);
  render() {
    const { mode, userInfo } = this.props;
    return (
      <div className="UserInfoContainer">
        <BasicInfo
          mode={mode}
          userInfo={userInfo}
          turnOnEditMode={this.turnOnEditMode}
          handleInputText={this.handleInputText}
          handleUpdateBtn={this.handleUpdateBtn}
        />
        <ContactInfo userInfo={this.props.userInfo} />
      </div>
    );
  }
}

export default UserInfo;
