import React, { Component } from 'react';
import './Skills.css';

class AddSkillBtn extends Component {
  changeSkillsMode = () => this.props.changeSkillsMode(true);
  render() {
    return <button onClick={this.changeSkillsMode}>+ Skill</button>;
  }
}

class Skills extends Component {
  changeSkillsMode = (boolean) => this.props.changeSkillsMode(boolean);
  render() {
    const { modes, componentsArray, skillsComponentInput } = this.props;
    const rows = [];
    let element;
    if (!modes.appMode.isEdit) {
      element = null;
    }
    if (modes.appMode.isEdit && !modes.skillsMode.isEdit) {
      element = (
        <AddSkillBtn
          changeWorkExperienceMode={this.changeWorkExperienceMode}
        />
      );
    }
    if (modes.appMode.isEdit && modes.workExperienceMode.isEdit) {
      element = (
        <AddExperienceForm
          workExperienceComponentInput={workExperienceComponentInput}
          changeWorkExperienceMode={this.changeWorkExperienceMode}
          handleInputChange={this.handleInputChange}
          addNewExperience={this.addNewExperience}
          removeWorkExperience={this.removeWorkExperience}
        />
      );
    }
    return (
      <div>
        <h2>Skills</h2>
        {rows}
        {element}
      </div>
    );
  }
}

export default Skills;
