class BasicInfo extends Component {
  turnOnEditMode = (e) => this.props.turnOnEditMode(e);
  render() {
    const userInfoArray = [];
    const {mode, userInfo} = this.props;
    for (const key in userInfo) {
      if (key === 'firstName'
        || key === 'lastName'
        || key === 'currentRole'
        || key === 'addr'
        || key === 'phone'
        || key === 'email'
        || key === 'github') {
          userInfoArray.push(key)
        } else return;
    }
    return <div>{userInfoArray}</div>;
  }
}

