  render() {
    return (
      <form>
        <input
          // type="text"
          // autoComplete="off"
          // onChange
          // value
        ></input>
        <button type="submit">
          Update
        </button>
      </form>
    );
  }
}

class InfoRow extends Component {
  render() {
    const { modes, info, inputUserInfo } = this.props;
    let element;
    if (modes.appMode.isEdit) {
      if (info.isEdit) {
        element = <InfoRowForm />
      } else {
          element = <li>{info.text} <span>âœŽ</span></li>
      }
    } else {
      element = <li>{info.text}</li>
    }
    return element;
  }
}

class BasicUserInfo extends Component {
  render() {
    const { modes, inputuserInfo, userInfoText } = this.props;
    const rows = [];
    let element;
    for (const [info, value] of Object.entries(userInfoText)) {
      console.log(value)
      if (info === 'textFirstName') {
        element = <InfoRow key={info.id} info={info} modes={modes} inputuserInfo={inputuserInfo}/>
      }
      if (info === 'textlastname') {
        element = <InfoRow key={info.id} info={info} modes={modes} inputuserInfo={inputuserInfo}/>
      }
      rows.push(element);
      return rows;
    }
    return (
      <div>
        <ul>
          {rows}
        </ul>
      </div>
    )
  }
}

