import React from 'react';
import Header from './components/Header';
import Builder from './components/Builder';
import Footer from './components/Footer';
import uniqid from 'uniqid';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inputText: '',
      inputCompanyNameText: '',
      inputCityText: '',
      inputFromText: '',
      inputToText: '',
      inputJobDescriptionText: '',
      inputSchoolText: '',
      inputSchoolCityText: '',
      inputSchoolFromYearText: '',
      inputSchoolToYearText: '',
      inputSchoolDegreeText: '',
      appMode: {
        isEdit: true
      },
      workExperienceMode: {
        isEdit: false
      },
      userInfoArray: [
        {
          name: 'firstName',
          text: 'First Name',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'lastName',
          text: 'Last Name',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'currentRole',
          text: 'Fullstack Javascript Developer',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'address',
          text: 'Houston, TX',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'phone',
          text: '(555) 555-5555',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'email',
          text: 'yourname@email.com',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'github',
          text: 'https://github.com',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        }
      ],
      workExperienceArray: []
    };
  }
  changeAppModeToEdit = () => {
    this.setState({
      appMode: {
        isEdit: true
      }
    });
  };
  changeAppModeToNonEdit = () => {
    this.setState({
      appMode: {
        isEdit: false
      }
    });
  };
  handleInputChange = (field, inputText) => {
    this.setState({
      [field]: inputText
    });
  };
  findClickedTarget = (element) => {
    const target = element.parentNode;
    const parent = Array.from(target.parentNode.children);
    return parent.indexOf(target);
  };
  handleUserInfoUpdateBtn = (field, element) => {
    const index = this.findClickedTarget(element);
    const tempArr = [...this.state.userInfoArray];
    tempArr[index].text = this.state[field];
    tempArr[index].mode.isEdit = false;
    this.setState({
      [field]: '',
      userInfoArray: tempArr
    });
  };
  changeInfoRowToEditMode = (element, arr) => {
    const index = this.findClickedTarget(element);
    const tempArr = [...arr];
    tempArr.forEach((item) => (item.mode.isEdit = false));
    tempArr[index].mode.isEdit = true;
    this.setState({
      [arr]: tempArr
    });
  };
  turnOffUserInfoEditMode = (arr) => {
    const tempArr = [...arr];
    tempArr.forEach((item) => (item.mode.isEdit = false));
    this.setState({
      [arr]: tempArr
    });
  };
  changeWorkExperienceMode = () => {
    let modeState = this.state.workExperienceMode.isEdit;
    modeState ? (modeState = false) : (modeState = true);
    this.setState({
      workExperienceMode: {
        isEdit: modeState
      }
    });
  };
  addNewExperience = () => {
    const {
      inputCompanyNameText,
      inputCityText,
      inputFromText,
      inputToText,
      inputJobDescriptionText
    } = this.state;
    const work = {
      id: uniqid(),
      companyNameText: inputCompanyNameText,
      cityText: inputCityText,
      fromText: inputFromText,
      toText: inputToText,
      jobDescriptionText: inputJobDescriptionText
    };
    this.setState({
      workExperienceArray: this.state.workExperienceArray.concat(work),
      workExperienceMode: {
        isEdit: false
      },
      inputCompanyNameText: '',
      inputCityText: '',
      inputFromText: '',
      inputToText: '',
      inputJobDescriptionText: ''
    });
  };
  removeWorkExperience = (element) => {
    const index = this.findClickedTarget(element);
    this.setState({
      workExperienceArray: this.state.workExperienceArray.splice(index, 1)
    });
  };
  render() {
    const {
      appMode,
      userInfoArray,
      workExperienceArray,
      workExperienceMode,
      inputText,
      inputCompanyNameText,
      inputCityText,
      inputFromText,
      inputToText,
      inputJobDescriptionText
    } = this.state;
    return (
      <div className="appContainer">
        <Header
          appMode={appMode}
          changeAppModeToEdit={this.changeAppModeToEdit}
          changeAppModeToNonEdit={this.changeAppModeToNonEdit}
        />
        <Builder
          appMode={appMode}
          inputText={inputText}
          inputCompanyNameText={inputCompanyNameText}
          inputCityText={inputCityText}
          inputFromText={inputFromText}
          inputToText={inputToText}
          inputJobDescriptionText={inputJobDescriptionText}
          userInfoArray={userInfoArray}
          workExperienceMode={workExperienceMode}
          workExperienceArray={workExperienceArray}
          changeInfoRowToEditMode={this.changeInfoRowToEditMode}
          handleInputChange={this.handleInputChange}
          handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
          changeWorkExperienceMode={this.changeWorkExperienceMode}
          turnOffUserInfoEditMode={this.turnOffUserInfoEditMode}
          addNewExperience={this.addNewExperience}
          removeWorkExperience={this.removeWorkExperience}
        />
        <Footer />
      </div>
    );
  }
}

export default App;
