import React, { Component } from 'react';

class InfoRowForm extends Component {
  handleInputChange = (e) => {
    this.props.handleInputChange(e.target.value);
  };
  handleUpdateBtn = (e) => {
    e.preventDefault();
    this.props.handleUpdateBtn(e.target);
  };
  render() {
    return (
      <form>
        <input
          type="text"
          autoComplete="off"
          onChange={this.handleInputChange}
          value={this.props.inputText}
        ></input>
        <button type="submit" onClick={this.handleUpdateBtn}>
          Update
        </button>
      </form>
    );
  }
}

class InfoRowNonEditMode extends Component {
  changeRowToEditMode = (e, userInfoArray) =>
    this.props.changeRowToEditMode(e.target, userInfoArray);
  render() {
    const info = this.props.info;
    return <p>{info.text}</p>;
  }
}

class InfoRow extends Component {
  changeRowToEditMode = (e) =>
    this.props.changeRowToEditMode(e.target);
  render() {
    const info = this.props.info;
    return (
      <p>
        {info.text}
        <span onClick={this.changeRowToEditMode}>âœŽ</span>
      </p>
    );
  }
}

class UserInfo extends Component {
  changeRowToEditMode = (element) => {
    this.props.changeRowToEditMode(element);
  };
  handleInputChange = (element) => {
    this.props.handleInputChange(element);
  };
  handleUpdateBtn = (element) => {
    this.props.handleUpdateBtn(element);
  };
  render() {
    const { appMode , userInfoArray } = this.props;
    const display = [];
    userInfoArray.map((info) => {
      if (appMode.isEdit && !info.mode.isEdit) {
        display.push(
          <InfoRow
            info={info}
            key={info.id}
            changeRowToEditMode={this.changeRowToEditMode}
          />
        );
      }
      if (appMode.isEdit && info.mode.isEdit) {
        display.push(
          <InfoRowForm
            info={info}
            key={info.id}
            inputText={this.props.inputText}
            handleInputChange={this.handleInputChange}
            handleUpdateBtn={this.handleUpdateBtn}
          />
        );
      }
      if (!appMode.isEdit && !info.mode.isEdit) {
        display.push(<InfoRowNonEditMode info={info} key={info.id} userInfoArray={userInfoArray}/>);
      }
      return display;
    });
    return <div className="UserInfoContainer">{display}</div>;
  }
}

export default UserInfo;
