class UserInfo extends Component {
  turnOnEditModeForFirstName = () => this.props.turnOnEditModeForFirstName();
  getNewFirstNameValue = (firstName) =>
    this.props.getNewFirstNameValue(firstName);
  handleUpdateBtn = () => this.props.handleUpdateBtn();
  render() {
    const userInfo = this.props.userInfo;
    return (
      <div className="UserInfoContainer">
        <BasicInfo
          userInfo={userInfo}
          turnOnEditModeForFirstName={this.turnOnEditModeForFirstName}
          getNewFirstNameValue={this.getNewFirstNameValue}
          handleUpdateBtn={this.handleUpdateBtn}
        />
        <ContactInfo userInfo={userInfo} />
      </div>
    );
  }
}

export default UserInfo;
