import React, { Component } from 'react';

class AddExperienceForm extends Component {
  changeWorkExperienceMode = (e) => {
    e.preventDefault();
    this.props.changeWorkExperienceMode();
  };
  handleInputChange = (e) => this.props.handleInputChange(e.target.value);
  addNewExperience = (e) => {
    e.preventDefault();
    this.props.addNewExperience();
  };
  render() {
    return (
      <form>
        <input
          type="text"
          placeholder="Company Name"
          autoComplete="off"
          name='companyName'
          value={this.props.inputCompanyNameText}
          onChange={this.handleInputChange}
        ></input>
        <input
          type="text"
          placeholder="City"
          autoComplete="off"
          name='city'
          value={this.props.inputCityText}
          onChange={this.handleInputChange}
        ></input>
        <input
          type="text"
          placeholder="From"
          autoComplete="off"
          name='from'
          value={this.props.inputFromText}
          onChange={this.handleInputChange}
        ></input>
        <input
          type="text"
          placeholder="To"
          autoComplete="off"

          value={this.props.inputToText}
          onChange={this.handleInputChange}
        ></input>
        <textarea
          rows="5"
          cols="30"
          placeholder="Job Description & Achievements"
          value={this.props.inputJobDescriptionText}
          onChange={this.handleInputChange}
        ></textarea>
        <button type="submit" onClick={this.changeWorkExperienceMode}>
          Cancel
        </button>
        <button type="submit" onClick={this.addNewExperience}>
          + Add
        </button>
      </form>
    );
  }
}
class AddExperienceBtn extends Component {
  changeWorkExperienceMode = () => this.props.changeWorkExperienceMode();
  render() {
    return (
      <button onClick={this.changeWorkExperienceMode}>+ Experience</button>
    );
  }
}

class WorkExperience extends Component {
  constructor(props) {
    super(props);
    this.state = {
      workExperienceMode: {
        isEdit: false
      }
    };
  }
  changeWorkExperienceMode = () => {
    let modeState = this.state.workExperienceMode.isEdit;
    modeState ? (modeState = false) : (modeState = true);
    this.setState({
      workExperienceMode: {
        isEdit: modeState
      }
    });
  };
  handleInputChange = (element) => this.props.handleInputChange(element);
  addNewExperience = () => this.props.addNewExperience();
  render() {
    const { appMode } = this.props;
    const { workExperienceMode } = this.state;
    const rows = [];
    let element;
    if (appMode.isEdit && !workExperienceMode.isEdit) {
      element = (
        <AddExperienceBtn
          changeWorkExperienceMode={this.changeWorkExperienceMode}
        />
      );
    }
    if (appMode.isEdit && workExperienceMode.isEdit) {
      element = (
        <AddExperienceForm
          inputText={this.props.inputText}
          changeWorkExperienceMode={this.changeWorkExperienceMode}
          handleInputChange={this.handleInputChange}
          addNewExperience={this.addNewExperience}
        />
      );
    }
    return (
      <div>
        <h2>Experiences</h2>
        {rows}
        {element}
      </div>
    );
  }
}

export default WorkExperience;
