import React, { Component } from 'react';

class AddExperienceForm extends Component {
  cancelWorkExperienceEditMode = () => this.props.cancelWorkExperienceEditMode();
  render() {
    return (
      <form>
        <input type="text" placeholder="Company Name"></input>
        <input type="text" placeholder="City"></input>
        <input type="text" placeholder="From"></input>
        <input type="text" placeholder="To"></input>
        <textarea rows='5' cols='30' placeholder='Job Description Achievements'></textarea>
        <button type="submit" onClick={this.cancelWorkExperienceEditMode}>Cancel</button>
        <button type="submit">+ Add</button>
      </form>
    );
  }
}
class AddExperienceBtn extends Component {
  changeWorkExperienceMode = () => this.props.changeWorkExperienceMode();
  render() {
    return (
      <button onClick={this.changeWorkExperienceMode}>+ Experience</button>
    );
  }
}

class WorkExperience extends Component {
  constructor(props) {
    super(props);
    this.state = {
      workExperienceMode: {
        isEdit: false
      }
    };
  }
  changeWorkExperienceMode = () => {
    this.setState({
      workExperienceMode: {
        isEdit: true
      }
    });
  };
  render() {
    const { appMode } = this.props;
    const { workExperienceMode } = this.state;
    const rows = [];
    let element;
    if (appMode.isEdit && !workExperienceMode.isEdit) {
      element = (
        <AddExperienceBtn
          changeWorkExperienceMode={this.changeWorkExperienceMode}
        />
      );
    }
    if (appMode.isEdit && workExperienceMode.isEdit) {
      element = <AddExperienceForm cancelWorkExperienceEditMode/>;
    }
    return (
      <div>
        <h2>Experiences</h2>
        {rows}
        {element}
      </div>
    );
  }
}

export default WorkExperience;
