import React, { Component } from 'react';
import uniqid from 'uniqid';

class UserInfo extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userInfoArray: [
        {
          name: 'firstName',
          text: 'First Name',
          id: uniqid(),
          mode: {
            isEdit: false,
            isView: true
          }
        },
        {
          name: 'lastName',
          text: 'Last Name',
          id: uniqid(),
          mode: {
            isEdit: false,
            isView: true
          }
        },
        {
          name: 'currentRole',
          text: 'Fullstack Javascript Developer',
          id: uniqid(),
          mode: {
            isEdit: false,
            isView: true
          }
        },
        {
          name: 'address',
          text: 'Houston, TX',
          id: uniqid(),
          mode: {
            isEdit: false,
            isView: true
          }
        },
        {
          name: 'phone',
          text: '(555) 555-5555',
          id: uniqid(),
          mode: {
            isEdit: false,
            isView: true
          }
        },
        {
          name: 'email',
          text: 'yourname@email.com',
          id: uniqid(),
          mode: {
            isEdit: false,
            isView: true
          }
        },
        {
          name: 'github',
          text: 'https://github.com',
          id: uniqid(),
          mode: {
            isEdit: false,
            isView: true
          }
        }
      ]
    };
  }
  changeUserInfoToEditMode = (e) => {
    const target = e.target.parentNode;
    const parent = Array.from(target.parentNode.children)
    const index = parent.indexOf(target);
    const element = this.state.userInfoArray[index];
    this.setState({
      [element]: {
        mode: {
          isEdit: true,
          isView: false
        }
      }
    })
    console.log(this.state.userInfoArray)
  }
  render() {
    const appMode = this.props.appMode;
    const display = this.state.userInfoArray.map((info) => {
      let element;
      if (appMode.isEdit && info.mode.isView) {
        element =
          <p key={info.id} className='userInfo'>
            {info.text}
            <span onClick={this.changeUserInfoToEditMode}>âœŽ</span>
          </p>
      }
      return element;
    });
    return <div className="UserInfoContainer">{display}</div>;
  }
}

export default UserInfo;
