import React, { Component } from 'react';
import './Skills.css';

class ExperienceHistoryRow extends Component {
  removeWorkExperience = (e) => this.props.removeWorkExperience(e.target);
  render() {
    const { modes, work } = this.props;
    let button;
    if (modes.appMode.isEdit) {
      button = <button type="submit" onClick={this.removeWorkExperience}>
      X
    </button>;
    } else button = null;
    return (
      <div className='experienceContainer'>
        <div>
          <p>
            {work.textCompanyName}, {work.textCompanyCity}
          </p>
        </div>
        <div>
          <p>
            {work.textCompanyFromYear} - {work.textCompanyToYear}
          </p>
          <p>{work.textCompanyRole}</p>
          <p>{work.textCompanyRoleDescription}</p>
        </div>
        {button}
      </div>
    );
  }
}

class AddSkillBtn extends Component {
  changeSkillsMode = () => this.props.changeSkillsMode(true);
  render() {
    return <button onClick={this.changeSkillsMode}>+ Skill</button>;
  }
}

class Skills extends Component {
  changeSkillsMode = (boolean) => this.props.changeSkillsMode(boolean);
  render() {
    const { modes, componentsArray, skillsComponentInput } = this.props;
    const rows = [];
    let element;
    if (!modes.appMode.isEdit) {
      element = null;
    }
    if (modes.appMode.isEdit && !modes.skillsMode.isEdit) {
      element = (
        <AddSkillBtn
          changeSkillsMode={this.changeSkillsMode}
        />
      );
    }
    if (modes.appMode.isEdit && modes.skillsMode.isEdit) {
      element = (
        <AddExperienceForm
          // workExperienceComponentInput={workExperienceComponentInput}
          // changeWorkExperienceMode={this.changeWorkExperienceMode}
          // handleInputChange={this.handleInputChange}
          // addNewExperience={this.addNewExperience}
          // removeWorkExperience={this.removeWorkExperience}
        />
      );
    }
    return (
      <div>
        <h2>Skills</h2>
        {rows}
        {element}
      </div>
    );
  }
}

export default Skills;
