import React, { Component } from 'react';
import './UserInfo.css';

class InfoRowForm extends Component {
  // handleInputChange = (e) => {
  //   this.props.handleInputChange('inputText', e.target.value);
  // };
  // handleUserInfoUpdateBtn = (e) => {
  //   e.preventDefault();
  //   this.props.handleUserInfoUpdateBtn('inputText', e.target);
  // };
  render() {
    return (
      <form>
        <input
        // type="text"
        // autoComplete="off"
        // onChange
        // value
        ></input>
        <button type="submit">Update</button>
      </form>
    );
  }
}

class InfoRow extends Component {
  changeInfoRowToEditMode = () => this.changeInfoRowToEditMode();
  render() {
    const { modes, text, inputUserInfo } = this.props;
    let element;
    if (modes.appMode.isEdit) {
      if (text.isEdit) {
        element = <InfoRowForm />;
      } else {
        element = (
          <li>
            {text} <span onClick={this.changeInfoRowToEditMode}>✎</span>
          </li>
        );
      }
    } else {
      element = <li>{text}</li>;
    }
    return element;
  }
}

class BasicUserInfo extends Component {
  render() {
    const { modes, inputUserInfo, userInfoText } = this.props;
    const rows = [];
    let element;
    for (const [info, value] of Object.entries(userInfoText)) {
      if (info === 'textFirstName'
      || info === 'textLastName') {
        element = (
          <InfoRow
            key={value.id}
            text={value.text}
            modes={modes}
            inputUserInfo={inputUserInfo}
          />
        );
        rows.push(element);
      }
    }
    return (
      <div>
        <ul>{rows}</ul>
      </div>
    );
  }
}

class ExtendedUserInfo extends Component {
  render() {
    const { modes, inputUserInfo, userInfoText } = this.props;
    const rows = [];
    let element;
    for (const [info, value] of Object.entries(userInfoText)) {
      if (info === 'textFirstName'
      || info === 'textLastName') {
        element = null;
      } else {
        console.log(info)
        element = (
          <InfoRow
            key={value.id}
            text={value.text}
            modes={modes}
            inputUserInfo={inputUserInfo}
          />
        );
        rows.push(element);
      }
    }
    return (
      <div>
        <ul>{rows}</ul>
      </div>
    );
  }
}

class UserInfo extends Component {
  // changeInfoRowToEditMode = (element, arr) => {
  //   this.props.changeInfoRowToEditMode(element, arr);
  // };
  // handleInputChange = (field, element) => {
  //   this.props.handleInputChange(field, element);
  // };
  // handleUserInfoUpdateBtn = (field, element) => {
  //   this.props.handleUserInfoUpdateBtn(field, element);
  // };
  render() {
    const { modes, inputs, textDisplay } = this.props;

    return (
      <div className="UserInfoContainer">
        <BasicUserInfo
          modes={modes}
          inputUserInfo={inputs.userInfoComponentInput.inputUserInfo}
          userInfoText={textDisplay.textUserInfoComponent}
        />
        <ExtendedUserInfo
          modes={modes}
          inputUserInfo={inputs.userInfoComponentInput.inputUserInfo}
          userInfoText={textDisplay.textUserInfoComponent}
        />
      </div>
    );
  }
}

export default UserInfo;
