import React, { Component } from 'react';
import uniqid from 'uniqid';

class InfoRowForm extends Component {
  handleInputChange = (e) => {
    this.props.handleInputChange(e.target.value);
  };
  handleUpdateBtn = (e) => {

  }
  render() {
    return (
      <form>
        <input
          type="text"
          autoComplete="off"
          onChange={this.handleInputChange}
          value={this.props.inputText}
        ></input>
        <button type="submit" onClick={this.handleUpdateBtn}>Update</button>
      </form>
    );
  }
}

class InfoRow extends Component {
  changeUserInfoToEditMode = (e) =>
    this.props.changeUserInfoToEditMode(e.target);
  render() {
    const info = this.props.info;
    return (
      <p>
        {info.text}
        <span onClick={this.changeUserInfoToEditMode}>âœŽ</span>
      </p>
    );
  }
}

class UserInfo extends Component {
  constructor(props) {
    super(props);
    this.state = {
      inputText: '',
      userInfoArray: [
        {
          name: 'firstName',
          text: 'First Name',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'lastName',
          text: 'Last Name',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'currentRole',
          text: 'Fullstack Javascript Developer',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'address',
          text: 'Houston, TX',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'phone',
          text: '(555) 555-5555',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'email',
          text: 'yourname@email.com',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'github',
          text: 'https://github.com',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        }
      ]
    };
  }
  changeUserInfoToEditMode = (element) => {
    const target = element.parentNode;
    const parent = Array.from(target.parentNode.children);
    const index = parent.indexOf(target);
    const newArray = [...this.state.userInfoArray];
    newArray.forEach((item) => (item.mode.isEdit = false));
    newArray[index].mode.isEdit = true;
    this.setState({
      userInfoArray: newArray
    });
  };
  handleInputChange = (inputText) => {
    this.setState({
      inputText: inputText
    });
  };
  render() {
    const appMode = this.props.appMode;
    const display = [];
    this.state.userInfoArray.map((info) => {
      if (!appMode.isEdit && !info.mode.isEdit) {
        display.push(
          <InfoRow
            info={info}
            key={info.id}
            changeUserInfoToEditMode={this.changeUserInfoToEditMode}
          />
        );
      }
      if (!appMode.isEdit && info.mode.isEdit) {
        display.push(
          <InfoRowForm
            info={info}
            key={info.id}
            handleInputChange={this.handleInputChange}
            inputText={this.state.inputText}
          />
        );
      }
      return display;
    });
    return <div className="UserInfoContainer">{display}</div>;
  }
}

export default UserInfo;
