            isEdit: false,
            isView: true
          }
        },
        phone: {
          text: '(555) 555-5555',
          id: uniqid(),
          mode: {
            isEdit: false,
            isView: true
          }
        },
        email: {
          text: 'yourname@email.com',
          id: uniqid(),
          mode: {
            isEdit: false,
            isView: true
          }
        },
        github: {
          text: 'https://github.com',
          id: uniqid(),
          mode: {
            isEdit: false,
            isView: true
          }
        }
      }
    };
  }
  turnOnEditMode = () => {

    // this.setState({
    //   mode: {
    //     isEdit: true,
    //     isPreview: false,
    //     isView: false
    //   }
    // });
  };
  handleInputText = (e) => {

  };
  handleUpdateBtn = (e) => {

  };
  render() {
    const {mode, userInfo} = this.state;
    return (
      <main>
        <UserInfo
          mode={mode}
          userInfo={userInfo}
          turnOnEditMode={this.turnOnEditMode}
          handleInputText={this.handleInputText}
          handleUpdateBtn={this.handleUpdateBtn}
        />
      </main>
    );
  }
}

export default Builder;
