import React, { Component } from 'react';
import './WorkExperience.css';

class ExperienceHistoryRow extends Component {
  removeWorkExperience = (e) => this.props.removeWorkExperience(e.target);
  render() {
    const { work } = this.props;
    return (
      <div>
        <div>
          <p>
            {work.textCompanyName}, {work.textCompanyCity}
          </p>
        </div>
        <div>
          <p>
            {work.textCompanyFromYear} - {work.textCompanyToYear}
          </p>
          <p>{work.textCompanyRole}</p>
          <p>{work.textCompanyRoleDescription}</p>
        </div>
        <button type="submit" onClick={this.removeWorkExperience}>
          X
        </button>
      </div>
    );
  }
}

class AddExperienceForm extends Component {
  changeWorkExperienceMode = (e) => {
    e.preventDefault();
    this.props.changeWorkExperienceMode();
  };
  handleInputChange = (e) => {
    let target = e.target.name;
    // let field;
    // if (target === 'companyName') {
    //   field = 'inputCompanyNameText';
    // } else if (target === 'city') {
    //   field = 'inputCityText';
    // } else if (target === 'from') {
    //   field = 'inputFromText';
    // } else if (target === 'to') {
    //   field = 'inputToText';
    // } else if (target === 'jobDescription') {
    //   field = 'inputJobDescriptionText';
    // }
    this.props.handleInputChange(target);
  };
  addNewExperience = (e) => {
    e.preventDefault();
    this.props.addNewExperience();
  };
  render() {
    const {
      inputCompanyName,
      inputCompanyCity,
      inputCompanyFrom,
      inputCompanyTo,
      inputCompanyRole,
     } = this.props.workExperienceComponentInput;
    const {
      textCompanyName,
      textCompanyCity,
      textCompanyFromYear,
      textCompanyToYear,
      textCompanyRole,
      textCompanyRoleDescription
    } = this.props.textWorkExperienceComponent;
    return (
      <form>
        <input
          type="text"
          placeholder="Company Name"
          autoComplete="off"
          name="textCompanyName"
          value={this.props.inputCompanyNameText}
          onChange={this.handleInputChange}
        ></input>
        <input
          type="text"
          placeholder="City"
          autoComplete="off"
          name="textCompanyCity"
          value={this.props.inputCityText}
          onChange={this.handleInputChange}
        ></input>
        <input
          type="text"
          placeholder="From"
          autoComplete="off"
          name="textCompanyFromYear"
          value={this.props.inputFromText}
          onChange={this.handleInputChange}
        ></input>
        <input
          type="text"
          placeholder="To"
          autoComplete="off"
          name="textCompanyToYear"
          value={this.props.inputToText}
          onChange={this.handleInputChange}
        ></input>
        <input
          type="text"
          placeholder="Role"
          autoComplete="off"
          name="textCompanyRole"
          value={this.props.inputToText}
          onChange={this.handleInputChange}
        ></input>
        <textarea
          rows="5"
          cols="30"
          name="textCompanyRoleDescription"
          placeholder="Job Description & Achievements"
          value={this.props.inputJobDescriptionText}
          onChange={this.handleInputChange}
        ></textarea>
        <button type="submit" onClick={this.changeWorkExperienceMode}>
          Cancel
        </button>
        <button type="submit" onClick={this.addNewExperience}>
          + Add
        </button>
      </form>
    );
  }
}

class AddExperienceBtn extends Component {
  changeWorkExperienceMode = () => this.props.changeWorkExperienceMode();
  render() {
    return (
      <button onClick={this.changeWorkExperienceMode}>+ Experience</button>
    );
  }
}

class WorkExperience extends Component {
  changeWorkExperienceMode = () => {
    this.props.changeWorkExperienceMode();
  };
  handleInputChange = (component, componentProp, inputText) =>
    this.props.handleInputChange(component, componentProp, inputText);
  addNewExperience = () => this.props.addNewExperience();
  removeWorkExperience = (element) => this.props.removeWorkExperience(element);
  render() {
    const {
      modes,
      componentsArray,
      workExperienceComponentInput,
      textWorkExperienceComponent
    } = this.props;
    const workExperienceArray = componentsArray.workExperienceArray;
    const rows = workExperienceArray.map((work) => (
      <ExperienceHistoryRow
        workExperienceArray={workExperienceArray}
        work={work}
        key={work.id}
        removeWorkExperience={this.removeWorkExperience}
      />
    ));
    let element;
    if (!modes.appMode.isEdit) {
      element = null;
    }
    if (modes.appMode.isEdit && !modes.workExperienceMode.isEdit) {
      element = (
        <AddExperienceBtn
          changeWorkExperienceMode={this.changeWorkExperienceMode}
        />
      );
    }
    if (modes.appMode.isEdit && modes.workExperienceMode.isEdit) {
      element = (
        <AddExperienceForm
          workExperienceComponentInput={workExperienceComponentInput}
          textWorkExperienceComponent={textWorkExperienceComponent}
          changeWorkExperienceMode={this.changeWorkExperienceMode}
          handleInputChange={this.handleInputChange}
          addNewExperience={this.addNewExperience}
        />
      );
    }
    return (
      <div>
        <h2>Experiences</h2>
        {rows}
        {element}
      </div>
    );
  }
}

export default WorkExperience;
