import React, { Component } from 'react';
import './Skills.css';

class AddSkillForm extends Component {
  handleInputChange = (e) => {
    this.props.handleInputChange(
      'skillsComponentInput',
      'inputSkillName',
      e.target.value
    );
  };
  handleUserInfoUpdateBtn = (e) => {
    e.preventDefault();
    this.props.handleUserInfoUpdateBtn( e.target);
  };
  render() {
    return (
      <form /* datakey={this.props.info} */>
        <input
          type="text"
          autoComplete="off"
          value={this.props.inputs}
          onChange={this.handleInputChange}
        ></input>
        <button type="submit" onClick={this.addNewSkill}>
          Update
        </button>
      </form>
    );
  }
}
class AddSkillBtn extends Component {
  changeSkillsMode = () => this.props.changeSkillsMode(true);
  render() {
    return <button onClick={this.changeSkillsMode}>+ Skill</button>;
  }
}

class Skills extends Component {
  changeSkillsMode = (boolean) => this.props.changeSkillsMode(boolean);
  handleInputChange = () => this.props.handleInputChange();
  render() {
    const { modes, componentsArray, skillsComponentInput } = this.props;
    const rows = [];
    let element;
    if (!modes.appMode.isEdit) {
      element = null;
    }
    if (modes.appMode.isEdit && !modes.skillsMode.isEdit) {
      element = <AddSkillBtn changeSkillsMode={this.changeSkillsMode} />;
    }
    if (modes.appMode.isEdit && modes.skillsMode.isEdit) {
      element = (
        <AddSkillForm
          skillsComponentInput={skillsComponentInput}
          handleInputChange={this.handleInputChange}
          // changeWorkExperienceMode={this.changeWorkExperienceMode}
          // addNewExperience={this.addNewExperience}
          // removeWorkExperience={this.removeWorkExperience}
        />
      );
    }
    return (
      <div>
        <h2>Skills</h2>
        {rows}
        {element}
      </div>
    );
  }
}

export default Skills;
