import React, { Component } from 'react';
import './UserInfo.css';

class InfoRowForm extends Component {
  handleInputChange = (e) => {

    this.props.handleInputChange(e.target.value);
  };
  handleUserInfoUpdateBtn = (e) => {
    e.preventDefault();
    this.props.handleUserInfoUpdateBtn('inputText', e.target);
  };
  render() {
    return (
      <form>
        <input
          type="text"
          autoComplete="off"
          value={this.props.inputs.userInfoComponentInput.inputUserInfo}
          onChange={this.handleInputChange}
        ></input>
        <button type="submit" onClick={this.handleUserInfoUpdateBtn}>
          Update
        </button>
      </form>
    );
  }
}

class InfoRow extends Component {
  changeInfoRowToEditMode = (e) => this.props.changeInfoRowToEditMode(e.target);
  handleInputChange = (obj, componentInput, inputText) =>
    this.props.handleInputChange(obj, componentInput, inputText);
  handleUserInfoUpdateBtn = (obj, componentInput, element) =>
    this.props.handleUserInfoUpdateBtn(obj, componentInput, element);
  render() {
    const { modes, info, value, inputs } = this.props;
    let element;
    if (modes.appMode.isEdit) {
      if (value.isEdit) {
        element = (
          <InfoRowForm
            inputs={inputs}
            handleInputChange={this.handleInputChange}
            handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
          />
        );
      } else {
        element = (
          <li datakey={info}>
            {value.text} <span onClick={this.changeInfoRowToEditMode}>✎</span>
          </li>
        );
      }
    } else {
      element = <li>{value.text}</li>;
    }
    return element;
  }
}

class BasicUserInfo extends Component {
  changeInfoRowToEditMode = (element) =>
    this.props.changeInfoRowToEditMode(element);
  handleInputChange = (obj, componentInput, inputText) =>
    this.props.handleInputChange(obj, componentInput, inputText);
  handleUserInfoUpdateBtn = (obj, componentInput, element) =>
    this.props.handleUserInfoUpdateBtn(obj, componentInput, element);
  render() {
    const { modes, inputs, userInfoText } = this.props;
    const rows = [];
    let element;
    for (const [info, value] of Object.entries(userInfoText)) {
      if (info === 'textFirstName' || info === 'textLastName') {
        element = (
          <InfoRow
            key={value.id}
            value={value}
            info={info}
            modes={modes}
            inputs={inputs}
            changeInfoRowToEditMode={this.changeInfoRowToEditMode}
            handleInputChange={this.handleInputChange}
            handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
          />
        );
        rows.push(element);
      }
    }
    return (
      <div>
        <ul>{rows}</ul>
      </div>
    );
  }
}

class ExtendedUserInfo extends Component {
  changeInfoRowToEditMode = (element) =>
    this.props.changeInfoRowToEditMode(element);
  handleInputChange = (obj, componentInput, inputText) =>
    this.props.handleInputChange(obj, componentInput, inputText);
  handleUserInfoUpdateBtn = (obj, componentInput, element) =>
    this.props.handleUserInfoUpdateBtn(obj, componentInput, element);
  render() {
    const { modes, inputs, userInfoText } = this.props;
    const rows = [];
    let element;
    for (const [info, value] of Object.entries(userInfoText)) {
      if (info === 'textFirstName' || info === 'textLastName') {
        continue;
      } else {
        element = (
          <InfoRow
            key={value.id}
            value={value}
            info={info}
            modes={modes}
            inputs={inputs}
            changeInfoRowToEditMode={this.changeInfoRowToEditMode}
            handleInputChange={this.handleInputChange}
            handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
          />
        );
        rows.push(element);
      }
    }
    return (
      <div>
        <ul>{rows}</ul>
      </div>
    );
  }
}

class UserInfo extends Component {
  changeInfoRowToEditMode = (element) =>
    this.props.changeInfoRowToEditMode(element);
  handleInputChange = (obj, componentInput, inputText) =>
    this.props.handleInputChange(obj, componentInput, inputText);
  handleUserInfoUpdateBtn = (obj, componentInput, element) =>
    this.props.handleUserInfoUpdateBtn(obj, componentInput, element);
  render() {
    const { modes, inputs, textDisplay } = this.props;

    return (
      <div className="UserInfoContainer">
        <BasicUserInfo
          modes={modes}
          inputs={inputs}
          userInfoText={textDisplay.textUserInfoComponent}
          changeInfoRowToEditMode={this.changeInfoRowToEditMode}
          handleInputChange={this.handleInputChange}
          handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
        />
        <ExtendedUserInfo
          modes={modes}
          inputs={inputs}
          userInfoText={textDisplay.textUserInfoComponent}
          changeInfoRowToEditMode={this.changeInfoRowToEditMode}
          handleInputChange={this.handleInputChange}
          handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
        />
      </div>
    );
  }
}

export default UserInfo;
