import React, { Component } from 'react';

class FirstNameForm extends Component {
  getNewFirstNameValue = (e) => {
    this.props.getNewFirstNameValue();
  };
  render() {
    const userInfo = this.props.userInfo;
    return (
      <form>
        <input
          type="text"
          id="firstName"
          placeholder="Enter your first name..."
          value={userInfo.firstName.text}
          autoComplete="off"
          onChange={this.getNewFirstNameValue}
        />
        <button type="submit">Update</button>
      </form>
    );
  }
}

class FirstNameText extends Component {
  turnOnEditModeForFirstName = () => {
    this.props.turnOnEditModeForFirstName();
  };
  render() {
    const userInfo = this.props.userInfo;
    return (
      <p>
        {userInfo.firstName.text}{' '}
        <span onClick={this.turnOnEditModeForFirstName}>âœŽ</span>
      </p>
    );
  }
}

class FirstNameControl extends Component {
  turnOnEditModeForFirstName = () => this.props.turnOnEditModeForFirstName();
  getNewFirstNameValue = () =>
    this.props.getNewFirstNameValue();
  render() {
    const userInfo = this.props.userInfo;
    if (userInfo.firstName.isEditMode) {
      return (
        <FirstNameForm
          userInfo={userInfo}
          getNewFirstNameValue={this.getNewFirstNameValue}
        />
      );
    } else
      return (
        <FirstNameText
          userInfo={userInfo}
          turnOnEditModeForFirstName={this.turnOnEditModeForFirstName}
        />
      );
  }
}

class BasicInfo extends Component {
  turnOnEditModeForFirstName = () => this.props.turnOnEditModeForFirstName();
  getNewFirstNameValue = () =>
    this.props.getNewFirstNameValue();
  render() {
    const userInfo = this.props.userInfo;
    return (
      <div className="BasicInfo">
        <FirstNameControl
          userInfo={userInfo}
          turnOnEditModeForFirstName={this.turnOnEditModeForFirstName}
          getNewFirstNameValue={this.getNewFirstNameValue}
        />
        {/* <p>{userInfo.lastName.text} âœŽ</p>
        <p>Current Role:</p>
        <p>{userInfo.currentRole.text} âœŽ</p> */}
      </div>
    );
  }
}

class ContactInfo extends Component {
  render() {
    const userInfo = this.props.userInfo;
    return (
      <div className="ContactInfo">
        {/* <p>Addr: {userInfo.addr.text} âœŽ</p>
        <p>ðŸ“ž: {userInfo.phone.text} âœŽ</p>
        <p>âœ‰: {userInfo.email.text} âœŽ</p>
        <p>Github: {userInfo.github.text} âœŽ</p> */}
      </div>
    );
  }
}

class UserInfo extends Component {
  turnOnEditModeForFirstName = () => this.props.turnOnEditModeForFirstName();
  getNewFirstNameValue = () =>
    this.props.getNewFirstNameValue();
  render() {
    const userInfo = this.props.userInfo;
    return (
      <div className="UserInfoContainer">
        <BasicInfo
          userInfo={userInfo}
          turnOnEditModeForFirstName={this.turnOnEditModeForFirstName}
          getNewFirstNameValue={this.getNewFirstNameValue}
        />
        <ContactInfo userInfo={userInfo} />
      </div>
    );
  }
}

export default UserInfo;
