import React from 'react';
import Header from './components/Header';
import Builder from './components/Builder';
import Footer from './components/Footer';
import uniqid from 'uniqid';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      inputText: '',
      inputCompanyNameText: '',
      inputCityText: '',
      inputFromText: '',
      inputToText: '',
      inputJobDescriptionText: '',
      appMode: {
        isEdit: true
      },
      userInfoArray: [
        {
          name: 'firstName',
          text: 'First Name',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'lastName',
          text: 'Last Name',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'currentRole',
          text: 'Fullstack Javascript Developer',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'address',
          text: 'Houston, TX',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'phone',
          text: '(555) 555-5555',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'email',
          text: 'yourname@email.com',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'github',
          text: 'https://github.com',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        }
      ],
      workExperienceArray: []
    };
  }
  changeAppModeToEdit = () => {
    this.setState({
      appMode: {
        isEdit: true
      }
    });
  };
  changeAppModeToNonEdit = () => {
    this.setState({
      inputText: '',
      appMode: {
        isEdit: false
      }
    });
  };
  handleInputChange = (field, inputText) => {
    this.setState({
      [field]: inputText
    });
  };
  findClickedTarget = (element) => {
    const target = element.parentNode;
    const parent = Array.from(target.parentNode.children);
    return parent.indexOf(target);
  };
  handleUserInfoUpdateBtn = (field, element) => {
    const target = element.parentNode;
    const parent = Array.from(target.parentNode.children);
    const index = parent.indexOf(target);
    // const index = this.findClickedTarget(element);
    const tempArr = [...this.state.userInfoArray];
    tempArr[index].text = this.state[field];
    tempArr[index].mode.isEdit = false;
    this.setState({
      [field]: '',
      userInfoArray: tempArr
    });
  };
  changeInfoRowToEditMode = (element, arr) => {
    const index = this.findClickedTarget(element);
    const tempArr = [...arr];
    tempArr.forEach((item) => (item.mode.isEdit = false));
    tempArr[index].mode.isEdit = true;
    this.setState({
      [arr]: tempArr
    });
  };
  addNewExperience = () => {

  }
  render() {
    const { appMode, userInfoArray, workExperienceArray } = this.state;
    return (
      <div className="appContainer">
        <Header
          appMode={appMode}
          changeAppModeToEdit={this.changeAppModeToEdit}
          changeAppModeToNonEdit={this.changeAppModeToNonEdit}
        />
        <Builder
          appMode={appMode}
          inputText={this.state.inputText}
          userInfoArray={userInfoArray}
          workExperienceArray={workExperienceArray}
          changeInfoRowToEditMode={this.changeInfoRowToEditMode}
          handleInputChange={this.handleInputChange}
          handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
          addNewExperience={this.addNewExperience}
        />
        <Footer />
      </div>
    );
  }
}

export default App;
