import React, { Component } from 'react';

class FirstNameForm extends Component {
  getNewFirstNameValue = (e) => {
    this.props.getNewFirstNameValue(e);
  };
  handleUpdateBtn = (e) => {
    e.preventDefault();
    this.props.handleUpdateBtn(e);
  }
  render() {
    const userInfo = this.props.userInfo;
    return (
      <form onSubmit={this.handleUpdateBtn}>
        <input
          type="text"
          id="firstName"
          placeholder="Enter your first name..."
          value={userInfo.firstName.text}
          autoComplete="off"
          onChange={this.getNewFirstNameValue}
        />
        <button type="submit">Update</button>
      </form>
    );
  }
}

