import React from 'react';
import Header from './components/Header';
import Builder from './components/Builder';
import Footer from './components/Footer';
import uniqid from 'uniqid';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      appMode: {
        isEdit: true
      },
      userInfoArray: [
        {
          name: 'firstName',
          text: 'First Name',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'lastName',
          text: 'Last Name',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'currentRole',
          text: 'Fullstack Javascript Developer',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'address',
          text: 'Houston, TX',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'phone',
          text: '(555) 555-5555',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'email',
          text: 'yourname@email.com',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        },
        {
          name: 'github',
          text: 'https://github.com',
          id: uniqid(),
          mode: {
            isEdit: false
          }
        }
      ]
    };
  }
  changeAppModeToEdit = () => {
    this.setState({
      appMode: {
        isEdit: true
      }
    });
  };
  changeAppModeToNonEdit = () => {
    this.setState({
      inputText: '',
      appMode: {
        isEdit: false
      }
    });
  };
  handleInputChange = (inputText) => {
    this.setState({
      inputText: inputText
    });
  };
  findClickedTarget = (element) => {
    const target = element.parentNode;
    const parent = Array.from(target.parentNode.children);
    const index = parent.indexOf(target);
    return index;
  };
  handleUserInfoUpdateBtn = (element) => {
    const index = this.findClickedTarget(element);
    // const target = element.parentNode;
    // const parent = Array.from(target.parentNode.children);
    // const index = parent.indexOf(target);
    const newArray = [...this.state.userInfoArray];
    newArray[index].text = this.state.inputText;
    newArray[index].mode.isEdit = false;
    this.setState({
      inputText: '',
      userInfoArray: newArray
    });
  };
  changeInfoRowToEditMode = (element, arr) => {
    const index = this.findClickedTarget(element);
    // const target = element.parentNode;
    // const parent = Array.from(target.parentNode.children);
    // const index = parent.indexOf(target);
    const newArray = [...arr];
    newArray.forEach((item) => (item.mode.isEdit = false));
    newArray[index].mode.isEdit = true;
    this.setState({
      [arr]: newArray
    });
  };

  render() {
    const { appMode, userInfoArray } = this.state;
    return (
      <div className="appContainer">
        <Header
          appMode={appMode}
          inputText={this.state.inputText}
          changeAppModeToEdit={this.changeAppModeToEdit}
          changeAppModeToNonEdit={this.changeAppModeToNonEdit}
        />
        <Builder
          appMode={appMode}
          userInfoArray={userInfoArray}
          changeInfoRowToEditMode={this.changeInfoRowToEditMode}
          handleInputChange={this.handleInputChange}
          handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
        />
        <Footer />
      </div>
    );
  }
}

export default App;
