import React from 'react';
import Header from './components/Header';
import Builder from './components/Builder';
import Footer from './components/Footer';
import './App.css';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      appMode: {
        isEdit: true
      }
    };
  }
  changeAppModeToEdit = () => {
    this.setState({
      appMode: {
        isEdit: true
      }
    });
  };
  render() {
    const appMode = this.state.appMode;
    return (
      <div className="appContainer">
        <Header
          appMode={appMode}
          changeAppModeToEdit={this.changeAppModeToEdit}
        />
        <Builder appMode={appMode} />
        <Footer />
      </div>
    );
  }
}

export default App;
