import React, { Component } from 'react';
import './Education.css'

class ExperienceHistoryRow extends Component {
  removeEducationHistory = (e) => this.props.removeEducationHistory(e.target);
  render() {
    const { education } = this.props;
    return (
      <div>
        <div>
          <p>
            {education.schoolText}, {education.schoolCityText}
          </p>
          <p>
            {education.schoolFromText} - {education.schoolToText}
          </p>
        </div>
        <div>
          <p>{education.schoolDegreeText}</p>
        </div>
        <button type="submit" onClick={this.removeEducationHistory}>
          X
        </button>
      </div>
    );
  }
}

class AddEducationForm extends Component {
  changeEducationMode = (e) => {
    e.preventDefault();
    this.props.changeEducationMode();
  };
  handleInputChange = (e) => {
    let target = e.target.name;
    let field;
    if (target === 'schoolName') {
      field = 'inputSchoolText';
    } else if (target === 'schoolCity') {
      field = 'inputSchoolCityText';
    } else if (target === 'schoolFrom') {
      field = 'inputSchoolFromYearText';
    } else if (target === 'schoolTo') {
      field = 'inputSchoolToYearText';
    } else if (target === 'schoolDegreeText') {
      field = 'inputSchoolDegreeText';
    }
    this.props.handleInputChange(field, e.target.value);
  };
  addNewEducation = (e) => {
    e.preventDefault();
    this.props.addNewEducation();
  };
  render() {
    return (
      <form>
        <input
          type="text"
          placeholder="School Name"
          autoComplete="off"
          name="schoolName"
          value={this.props.inputSchoolText}
          onChange={this.handleInputChange}
        ></input>
        <input
          type="text"
          placeholder="School City"
          autoComplete="off"
          name="schoolCity"
          value={this.props.inputSchoolCityText}
          onChange={this.handleInputChange}
        ></input>
        <input
          type="text"
          placeholder="From"
          autoComplete="off"
          name="schoolFrom"
          value={this.props.inputSchoolFromYearText}
          onChange={this.handleInputChange}
        ></input>
        <input
          type="text"
          placeholder="To"
          autoComplete="off"
          name="schoolTo"
          value={this.props.inputSchoolToYearText}
          onChange={this.handleInputChange}
        ></input>
        <input
          type="text"
          placeholder="Degree"
          autoComplete="off"
          name="schoolDegreeText"
          value={this.props.inputSchoolDegreeText}
          onChange={this.handleInputChange}
        ></input>
        <button type="submit" onClick={this.changeEducationMode}>
          Cancel
        </button>
        <button type="submit" onClick={this.addNewEducation}>
          + Add
        </button>
      </form>
    );
  }
}
class AddEducationBtn extends Component {
  changeEducationMode = () => this.props.changeEducationMode();
  render() {
    return (
      <button onClick={this.changeEducationMode}>+ Education</button>
    );
  }
}

class Education extends Component {
  changeEducationMode = () => {
    this.props.changeEducationMode();
    // this.props.turnOffUserInfoEditMode(this.props.educationArray);
  };
  handleInputChange = (field, element) =>
    this.props.handleInputChange(field, element);
  addNewEducation = () => this.props.addNewEducation();
  removeEducationHistory = (element) => this.props.removeEducationHistory(element);
  render() {
    const {
      appMode,
      educationMode,
      educationArray,
      inputSchoolText,
      inputSchoolCityText,
      inputSchoolFromYearText,
      inputSchoolToYearText,
      inputSchoolDegreeText
    } = this.props;
    const rows = this.props.educationArray.map((education) => (
      <ExperienceHistoryRow
        educationArray={educationArray}
        education={education}
        key={education.id}
        removeEducationHistory={this.removeEducationHistory}
      />
    ));
    let element;
    if (!appMode.isEdit) { element = null};
    if (appMode.isEdit && !educationMode.isEdit) {
      element = (
        <AddEducationBtn
          changeEducationMode={this.changeEducationMode}
        />
      );
    }
    if (appMode.isEdit && educationMode.isEdit) {
      element = (
        <AddEducationForm
          inputSchoolText={inputSchoolText}
          inputSchoolCityText={inputSchoolCityText}
          inputSchoolFromYearText={inputSchoolFromYearText}
          inputSchoolToYearText={inputSchoolToYearText}
          inputSchoolDegreeText={inputSchoolDegreeText}
          changeEducationMode={this.changeEducationMode}
          handleInputChange={this.handleInputChange}
          addNewEducation={this.addNewEducation}
        />
      );
    }
    return (
      <div>
        <h2>Education</h2>
        {rows}
        {element}
      </div>
    );
  }
}

export default Education;
