import React, { Component } from 'react';
import './UserInfo.css';

class InfoRowForm extends Component {
  handleInputChange = (e) => {
    console.log(this.props.inputUserInfo)
    this.props.handleInputChange(this.props.inputUserInfo, e.target.value);
  };
  handleUserInfoUpdateBtn = (e) => {
    e.preventDefault();
    this.props.handleUserInfoUpdateBtn('inputText', e.target);
  };
  render() {
    return (
      <form>
        <input
          type="text"
          autoComplete="off"

          onChange={this.handleInputChange}
        ></input>
        <button type="submit" onClick={this.handleUserInfoUpdateBtn}>
          Update
        </button>
      </form>
    );
  }
}

class InfoRow extends Component {
  changeInfoRowToEditMode = (e) => this.props.changeInfoRowToEditMode(e.target);
  handleInputChange = (objInput, inputText) =>
    this.props.handleInputChange(objInput, inputText);
  handleUserInfoUpdateBtn = (objInput, inputText) =>
    this.props.handleUserInfoUpdateBtn(objInput, inputText);
  render() {
    const { modes, info, value, inputUserInfo } = this.props;
    let element;
    if (modes.appMode.isEdit) {
      if (value.isEdit) {
        element = (
          <InfoRowForm
            inputUserInfo={inputUserInfo}
            handleInputChange={this.handleInputChange}
            handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
          />
        );
      } else {
        element = (
          <li datakey={info}>
            {value.text} <span onClick={this.changeInfoRowToEditMode}>âœŽ</span>
          </li>
        );
      }
    } else {
      element = <li>{value.text}</li>;
    }
    return element;
  }
}

class BasicUserInfo extends Component {
  changeInfoRowToEditMode = (element) =>
    this.props.changeInfoRowToEditMode(element);
  handleInputChange = (objInput, inputText) =>
    this.props.handleInputChange(objInput, inputText);
  handleUserInfoUpdateBtn = (objInput, inputText) =>
    this.props.handleUserInfoUpdateBtn(objInput, inputText);
  render() {
    const { modes, inputUserInfo, userInfoText } = this.props;
    const rows = [];
    let element;
    for (const [info, value] of Object.entries(userInfoText)) {
      if (info === 'textFirstName' || info === 'textLastName') {
        element = (
          <InfoRow
            key={value.id}
            value={value}
            info={info}
            modes={modes}
            inputUserInfo={inputUserInfo}
            changeInfoRowToEditMode={this.changeInfoRowToEditMode}
            handleInputChange={this.handleInputChange}
            handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
          />
        );
        rows.push(element);
      }
    }
    return (
      <div>
        <ul>{rows}</ul>
      </div>
    );
  }
}

class ExtendedUserInfo extends Component {
  changeInfoRowToEditMode = (element) =>
    this.props.changeInfoRowToEditMode(element);
  handleInputChange = (objInput, inputText) =>
    this.props.handleInputChange(objInput, inputText);
  handleUserInfoUpdateBtn = (objInput, inputText) =>
    this.props.handleUserInfoUpdateBtn(objInput, inputText);
  render() {
    const { modes, inputUserInfo, userInfoText } = this.props;
    const rows = [];
    let element;
    for (const [info, value] of Object.entries(userInfoText)) {
      if (info === 'textFirstName' || info === 'textLastName') {
        continue;
      } else {
        element = (
          <InfoRow
            key={value.id}
            value={value}
            info={info}
            modes={modes}
            inputUserInfo={inputUserInfo}
            changeInfoRowToEditMode={this.changeInfoRowToEditMode}
            handleInputChange={this.handleInputChange}
            handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
          />
        );
        rows.push(element);
      }
    }
    return (
      <div>
        <ul>{rows}</ul>
      </div>
    );
  }
}

class UserInfo extends Component {
  changeInfoRowToEditMode = (element) =>
    this.props.changeInfoRowToEditMode(element);
  handleInputChange = (objInput, inputText) =>
    this.props.handleInputChange(objInput, inputText);
  handleUserInfoUpdateBtn = (objInput, inputText) =>
    this.props.handleUserInfoUpdateBtn(objInput, inputText);
  render() {
    const { modes, userInfoComponentInput, textDisplay } = this.props;

    return (
      <div className="UserInfoContainer">
        <BasicUserInfo
          modes={modes}
          inputUserInfo={inputUserInfo}
          userInfoText={textDisplay.textUserInfoComponent}
          changeInfoRowToEditMode={this.changeInfoRowToEditMode}
          handleInputChange={this.handleInputChange}
          handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
        />
        <ExtendedUserInfo
          modes={modes}
          inputUserInfo={inputUserInfo}
          userInfoText={textDisplay.textUserInfoComponent}
          changeInfoRowToEditMode={this.changeInfoRowToEditMode}
          handleInputChange={this.handleInputChange}
          handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
        />
      </div>
    );
  }
}

export default UserInfo;
