import React, { Component } from 'react';

class InfoRowForm extends Component {
  handleInputChange = (e) => {
    this.props.handleInputChange('input', e.target.value);
  };
  handleUserInfoUpdateBtn = (e) => {
    e.preventDefault();
    this.props.handleUserInfoUpdateBtn(this.props.inputText, e.target);
  };
  render() {
    console.log(this.props.inputText)
    return (
      <form>
        <input
          type="text"
          autoComplete="off"
          onChange={this.handleInputChange}
          value={this.props.inputText}
        ></input>
        <button type="submit" onClick={this.handleUserInfoUpdateBtn}>
          Update
        </button>
      </form>
    );
  }
}

class InfoRowNonEditMode extends Component {
  render() {
    const info = this.props.info;
    return <p>{info.text}</p>;
  }
}

class InfoRow extends Component {
  changeInfoRowToEditMode = (e) =>
    this.props.changeInfoRowToEditMode(e.target, this.props.userInfoArray);
  render() {
    const info = this.props.info;
    return (
      <p>
        {info.text}
        <span onClick={this.changeInfoRowToEditMode}>âœŽ</span>
      </p>
    );
  }
}

class UserInfo extends Component {
  changeInfoRowToEditMode = (element, arr) => {
    this.props.changeInfoRowToEditMode(element, arr);
  };
  handleInputChange = (field, element) => {
    this.props.handleInputChange(field, element);
  };
  handleUserInfoUpdateBtn = (field, element) => {
    this.props.handleUserInfoUpdateBtn(field, element);
  };
  render() {
    const { appMode , userInfoArray } = this.props;
    const display = [];
    userInfoArray.map((info) => {
      if (appMode.isEdit && !info.mode.isEdit) {
        display.push(
          <InfoRow
            info={info}
            key={info.id}
            userInfoArray={userInfoArray}
            changeInfoRowToEditMode={this.changeInfoRowToEditMode}
          />
        );
      }
      if (appMode.isEdit && info.mode.isEdit) {
        display.push(
          <InfoRowForm
            info={info}
            key={info.id}
            inputText={this.props.inputText}
            handleInputChange={this.handleInputChange}
            handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
          />
        );
      }
      if (!appMode.isEdit && !info.mode.isEdit) {
        display.push(<InfoRowNonEditMode info={info} key={info.id} />);
      }
      return display;
    });
    return <div className="UserInfoContainer">{display}</div>;
  }
}

export default UserInfo;
