import React, { Component } from 'react';
import UserInfo from './UserInfo';
import './Builder.css';

class Builder extends Component {
  constructor(props) {
    super(props);
    this.state = {
      mode: 'view',
      userInfo: {
        firstName: {
          text: 'First Name',
        },
        lastName: {
          text: 'Last Name',
          inputText: '',
          isEditMode: false
        },
        currentRole: {
          text: 'Full Stack Developer',
          inputText: '',
          isEditMode: false
        },
        addr: {
          text: 'Houston, TX',
          inputText: '',
          isEditMode: false
        },
        phone: {
          text: '(555) 555-5555',
          inputText: '',
          isEditMode: false
        },
        email: {
          text: 'yourname@email.com',
          inputText: '',
          isEditMode: false
        },
        github: {
          text: 'https://github.com',
          inputText: '',
          isEditMode: false
        }
      }
    };
  }
  turnOnEditModeForFirstName = () => {
    this.setState({
      userInfo: {
        firstName: {
          isEditMode: true,
          text: '',
          inputText: ''
        }
      }
    });
  };
  getNewFirstNameValue = (e) => {
    this.setState({
      userInfo: {
        firstName: {
          isEditMode: true,
          text: e.target.value
        }
      }
    });
  };
  handleUpdateBtn = (e) => {

  };
  render() {
    const userInfo = this.state.userInfo;
    return (
      <main>
        <UserInfo
          userInfo={userInfo}
          turnOnEditModeForFirstName={this.turnOnEditModeForFirstName}
          getNewFirstNameValue={this.getNewFirstNameValue}
          handleUpdateBtn={this.handleUpdateBtn}
        />
      </main>
    );
  }
}

export default Builder;
