import React, { Component } from 'react';

class FirstNameForm extends Component {
  render() {
    return (
      <form>
        <input
          type="text"
          id="firstName"
          placeholder="Enter your first name..."
          value={this.props.userInfo.firstName.text}
        />
        <button type="submit">Update</button>
      </form>
    );
  }
}

class FirstNameText extends Component {
  turnOnEditModeForFirstName = () => {
    this.props.turnOnEditModeForFirstName();
  }
  render() {
    const userInfo = this.props.userInfo;
    return (
      <p>{userInfo.firstName.text} <span onChange={this.turnOnEditModeForFirstName}>âœŽ</span></p>
    );
  }
}

class FirstNameControl extends Component {
  render() {
    const userInfo = this.props.userInfo;
    if (userInfo.firstName.isEditMode) {
      return <FirstNameForm userInfo={userInfo}/>
    } else return <FirstNameText userInfo={userInfo} turnOnEditModeForFirstName/>

  }
}

class BasicInfo extends Component {
  changeFirstName = () => {
    this.props.basicInfoChangeFirstName();
  };
  render() {
    const userInfo = this.props.userInfo;
    return (
      <div className="BasicInfo">
        <FirstNameControl userInfo={userInfo}/>
        <p>{userInfo.lastName.text} âœŽ</p>
        <p>Current Role:</p>
        <p>{userInfo.currentRole.text} âœŽ</p>
      </div>
    );
  }
}

class ContactInfo extends Component {
  render() {
    const userInfo = this.props.userInfo;
    return (
      <div className="ContactInfo">
        <p>Addr: {userInfo.addr.text} âœŽ</p>
        <p>ðŸ“ž: {userInfo.phone.text} âœŽ</p>
        <p>âœ‰: {userInfo.email.text} âœŽ</p>
        <p>Github: {userInfo.github.text} âœŽ</p>
      </div>
    );
  }
}

class UserInfo extends Component {
  changeFirstName = () => this.props.userInfoChangeFirstName();
  render() {
    const userInfo = this.props.userInfo;
    return (
      <div className="UserInfoContainer">
        <BasicInfo
          userInfo={userInfo}
          basicInfoChangeFirstName={this.changeFirstName}
        />
        <ContactInfo userInfo={userInfo} />
      </div>
    );
  }
}

export default UserInfo;
