import React, { Component } from 'react';
import UserInfo from './UserInfo';
import WorkExperience from './WorkExperience';
import './Builder.css';
import Education from './Education';

class Builder extends Component {
  changeInfoRowToEditMode = (element, arr) => {
    this.props.changeInfoRowToEditMode(element, arr);
  };
  handleInputChange = (field, element) => {
    this.props.handleInputChange(field, element);
  };
  handleUserInfoUpdateBtn = (field, element) => {
    this.props.handleUserInfoUpdateBtn(field, element);
  };
  turnOffUserInfoEditMode = (arr) => this.props.turnOffUserInfoEditMode(arr);
  changeWorkExperienceMode = () => this.props.changeWorkExperienceMode();
  addNewExperience = () => this.props.addNewExperience();
  removeWorkExperience = (element) => this.props.removeWorkExperience(element);
  changeEducationMode = () => this.props.changeEducationMode();
  addNewEducation = () => this.props.addNewEducation();
  removeEducationHistory = (element) => this.props.removeEducationHistory(element);
  render() {
    const {

    } = this.props;
    return (
      <main>
        <UserInfo
          appMode={appMode}
          inputText={inputText}
          userInfoArray={userInfoArray}
          changeInfoRowToEditMode={this.changeInfoRowToEditMode}
          handleInputChange={this.handleInputChange}
          handleUserInfoUpdateBtn={this.handleUserInfoUpdateBtn}
        />
        <WorkExperience
          appMode={appMode}
          userInfoMode={userInfoMode}
          workExperienceMode={workExperienceMode}
          educationMode={educationMode}
          userInfoArray={userInfoArray}
          workExperienceArray={workExperienceArray}
          inputCompanyNameText={inputCompanyNameText}
          inputCityText={inputCityText}
          inputFromText={inputFromText}
          inputToText={inputToText}
          handleInputChange={this.handleInputChange}
          turnOffUserInfoEditMode={this.turnOffUserInfoEditMode}
          addNewExperience={this.addNewExperience}
          changeWorkExperienceMode={this.changeWorkExperienceMode}
          removeWorkExperience={this.removeWorkExperience}
        />
        <Education
          appMode={appMode}
          userInfoMode={userInfoMode}
          workExperienceMode={workExperienceMode}
          educationMode={educationMode}
          userInfoArray={userInfoArray}
          workExperienceArray={workExperienceArray}
          educationArray={educationArray}
          inputSchoolText={inputSchoolText}
          inputSchoolCityText={inputSchoolCityText}
          inputSchoolFromYearText={inputSchoolFromYearText}
          inputSchoolToYearText={inputSchoolToYearText}
          inputSchoolDegreeText={inputSchoolDegreeText}
          handleInputChange={this.handleInputChange}
          changeEducationMode={this.changeEducationMode}
          addNewEducation={this.addNewEducation}
          removeEducationHistory={this.removeEducationHistory}
        />
      </main>
    );
  }
}

export default Builder;
